// 当前这个模块：API进行统一管理
import requests from "./request";
import mockRequests from './mockAjax'

// 三级联动的接口
// /api/product/getBaseCategoryList  get 
// 发请求 :axios发请求返回结果Promise对象
export const reqCategoryList =()=>requests({url:'/product/getBaseCategoryList',method:'get'})

// 获取banner（home首页轮播图的接口）
export const reqGetBannerList =()=>mockRequests.get('/banner')

// 获取floor数据
export const reqFloorList=()=>mockRequests.get('/floor')

// 获取搜索模块数据 地址：/api/list post 需要带参数
/* {
    "category3Id": "61",
    "categoryName": "手机",
    "keyword": "小米",
    "order": "1:desc",
    "pageNo": 1,
    "pageSize": 10,
    "props": ["1:1700-2799:价格", "2:6.65-6.74英寸:屏幕尺寸"],
    "trademark": "4:小米"
  } */
// 当前这个函数需不需要接受外部传递参数
// 当前这个接口，给服务器传递参数params，至少是一个空对象
  export const reqGetSearchInfo =(params)=>requests({url:'/list',method:'post',data:params})
  

  // 获取产品详细信息的接口 /api/item/{ skuId } get
  export const reqGoodsInfo=(skuId)=>requests({url:`/item/${ skuId }`,method:'get'})

  // 将产品添加到购物车中（获取更新某一个产品的个数）
  // /api/cart/addToCart/{ skuId }/{ skuNum } post
  export const reqAddOrUpdateShopCart=(skuId,skuNum)=>requests({url:`/cart/addToCart/${ skuId }/${ skuNum }`,method:'post'})

  //获取购物车列表数据接口
  // /api/cart/cartList get
  export const reqCartList =()=>requests({url:'/cart/cartList',method:'get'})

  // 删除购物车商品的接口
  // /api/cart/deleteCart/{skuId} delete
  export const reqDeleteCartById=(skuId)=>requests({url:`/cart/deleteCart/${skuId}`,method:'delete'})

  // 修改购物车中商品选中的状态
  // /api/cart/checkCart/{skuID}/{isChecked} get
  export const reqUpdateCheckedByid = (skuId,isChecked)=>requests({url:`/cart/checkCart/${skuId}/${isChecked}`,method:'get'})

  // 获取验证码
  // /api/user/passport/sendCode/{phone} get
  export const reqGetCode=(phone)=>requests({url:`/user/passport/sendCode/${phone}`,method:'get'})

  // 注册
  // /api/user/passport/register  post  phone code password
  export const reqUserRegister=(data)=>requests({url:"/user/passport/register",data,method:'post'})

  // 登录
  // /api/user/passport/login post phone password
  export const reqUserLogin =(data)=>requests({url:'/user/passport/login',data,method:'post'})

  // 获取用户信息【需要带着用户的token向服务器获取用户信息】
  // /api/user/passport/auth/getUserInfo get
  export const reqUserInfo =()=>requests({url:'/user/passport/auth/getUserInfo',method:'get'})

  // 退出登录
  // /api/user/passport/logout get
  export const reqLogout=()=>requests({url:'/user/passport/logout',method:'get'})

  // 获取用户地址信息
  // /api/user/userAddress/auth/findUserAddressList get
  export const reqAddressInfo=()=>requests({url:'/user/userAddress/auth/findUserAddressList',method:'get'})

  // 获取商品清单
  // /api/order/auth/trade get
  export const reqOrderInfo = ()=>requests({url:'/order/auth/trade',method:'get'})

  // 提交订单的接口
  // /api/order/auth/submitOrder?tradeNo={tradeNo} post
  export const reqSubmitOrder=(tradeNo,data)=>requests({url:`/order/auth/submitOrder?tradeNo=${tradeNo}`,data,method:'post'})

  // 获取支付信息
  // /api/payment/weixin/createNative/{orderId} get
  export const reqPayInfo=(orderId)=>requests({url:`/payment/weixin/createNative/${orderId}`,method:'get'})
