// 订单支付
import {reqAddressInfo} from '@/api'
const state={}
const mutations={}
const actions={
    // 获取用户地址信息
    async getUserAddress({commit}){
        let result = await reqAddressInfo();
        if(result.code==200){
            commit('GETUSERADDRESS',result.data)
        }
    }
}
const getters={}
export default{
    state,mutations,actions,getters
}
