export default {
  name: 'Login',

  data() {
    return {
      phone: '',
      password: ''
    };
  },

  methods: {
    // 登录
    async userLogin() {
      try {
        // 登录成功
        const {
          phone,
          password
        } = this;
        phone && password && (await this.$store.dispatch('userLogin', {
          phone,
          password
        })); // 登录的路由组件：看路由当中是否包含query参数 有：跳转到query参数指定的路由，没有：跳到home

        let toPath = this.$route.query.redirect || '/home';
        this.$router.push(toPath);
      } catch (error) {
        alert(error.message);
      }
    }

  }
};
