// 引入路由组件
import Home from '@/pages/Home'
import Login from '@/pages/Login'
import Register from '@/pages/Register'
import Search from '@/pages/Search'
import Detail from '@/pages/Detail'
import AddCartSuccess from "@/pages/AddCartSuccess";
import ShopCart from '@/pages/ShopCart';
import Trade from '@/pages/Trade';
import Pay from '@/pages/Pay';
import PaySuccess from '@/pages/PaySuccess'
import Center from '@/pages/Center'
// 路由配置信息
export default [
    // 路由重定向，在项目跑起来的时候，默认访问首页
    {
        path:'*',
        redirect:'/home'
    },
    {
        path:"/home",
        component:Home,
        meta:{show:true}
    },
    {
        path:"/search/:keyword?",
        component:Search,
        meta:{show:true},
        name:'search',
        // 路由组件能不能传递props数据?
        // 布尔值写法：params
        // props:true
        // 对象写法:额外给路由组件传递一些props
        // props:{a:1,b:2}
        // 函数写法：可以params参数，query参数，通过props传递给路由组件
        props:($route)=>{
            return {keyword:$route.params.keyword,k:$route.query.k}
        }
    },
    {
        path:"/login",
        component:Login,
        meta:{show:false}
    },
    {
        path:"/register",
        component:Register,
        meta:{show:false}
    },
    {
        path:"/detail/:skuid",
        component:Detail,
        meta:{show:true}
    },
    {
        path:"/addcartsuccess",
        name:'addcartsuccess',
        component:AddCartSuccess,
        meta:{show:true}
    },
    {
        path:"/shopcart",
        name:'shopcart',
        component:ShopCart,
        meta:{show:true}
    }, 
     {
        path:"/trade",
        name:'trade',
        component:Trade,
        meta:{show:true}
    },
    {
        path:"/pay",
        name:'pay',
        component:Pay,
        meta:{show:true}
    },
    {
        path:"/paysuccess",
        name:'paysuccess',
        component:PaySuccess,
        meta:{show:true}
    },
    {
        path:"/center",
        name:'center',
        component:Center,
        meta:{show:true}
    },
]
